import React, { useContext } from 'react'
import { View, Text, FlatList, StyleSheet, Image } from 'react-native'
import { CartContext } from '../context/CartContext'
import COLORS from '../assets/colors/colors';

export default function shoppingCartIcon() {

    const [cart, setCart] = useContext(CartContext);


    const totalPrice = cart.reduce((acc, curr) => acc + curr.price, 0)

    // const cartName = this.cart.data.map(function (data, id) {
    //     return key = { id }{ data.name }

    // }

    return (
        <View>


            <FlatList
                showsVerticalScrollIndicator={false}
                data={cart}
                renderItem={({ item }) =>

                    <View style={styles.main}>

                        <View style={styles.withinMain}>
                            <View style={styles.imageBoundary}>
                                <Image style={{
                                    flex: 1,
                                    resizeMode: 'cover',
                                    borderRadius: 10,
                                    height: 90,
                                    width: 80,
                                }}
                                    imageStyle={{
                                        width: 80
                                    }}
                                    source={item.image} />
                            </View>
                            <View style={styles.textBoundary}>
                                <Text style={styles.topText}>{item.name}</Text>
                                <Text style={styles.midText}>{item.description} {'\n'}{item.description2}</Text>
                                <Text style={styles.downText}>{item.price}</Text>
                            </View>

                        </View>


                    </View>
                }
            />
        </View>
    )
}

const styles = StyleSheet.create({
    main: {
        backgroundColor: COLORS.white,
        width: '85%',
        borderRadius: 5,
        height: 100,
        marginBottom: 12,
        alignSelf: 'center'


    },
    withinMain: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        margin: 5
    },
    imageBoundary: {
        height: 90,
        width: 80,
        borderRadius: 10
    },
    textBoundary: {

        justifyContent: 'space-between',
        height: 90,
        borderRadius: 10
    },
    topText: {
        fontWeight: 'bold',
        fontSize: 18
    },
    midText: {
        color: 'grey',
        fontSize: 13
    },
    downText: {
        color: COLORS.green,
        fontWeight: 'bold',
        fontSize: 18
    }
})
