import React from 'react'
import { View, Text, StyleSheet, TouchableOpacity, Image, FlatList } from 'react-native'
import COLORS from '../../assets/colors/colors'
import * as Icons from "react-native-heroicons/solid";


export default function Item() {


    const list = [
        {
            id: 1,
            topText: 'Chicken Burger',
            midText1: 'Spicy chicken burger ',
            midText2: 'Taste great and beyond',
            price: '39.99',
            img: require('../../assets/bg1.jpg'),

        },
        {
            id: 2,
            topText: 'Pizza',
            midText1: 'Spicy chicken burger ',
            midText2: 'Taste great and beyond',
            price: '39.99',
            img: require('../../assets/bg2.jpg'),

        },
        {
            id: 3,
            topText: 'Variety',
            midText1: 'Spicy chicken burger ',
            midText2: 'Taste great and beyond',
            price: '39.99',
            img: require('../../assets/blackboard.jpg'),

        },
        {
            id: 4,
            topText: 'Chicken Burger',
            midText1: 'Spicy chicken burger ',
            midText2: 'Taste great and beyond',
            price: '39.99',
            img: require('../../assets/bg1.jpg'),

        },
        {
            id: 5,
            topText: 'Chicken Burger',
            midText1: 'Spicy chicken burger ',
            midText2: 'Taste great and beyond',
            price: '39.99',
            img: require('../../assets/bg1.jpg'),

        }, {
            id: 6,
            topText: 'Chicken Burger',
            midText1: 'Spicy chicken burger ',
            midText2: 'Taste great and beyond',
            price: '39.99',
            img: require('../../assets/bg1.jpg'),

        },
    ]


    const Menu = ({ list }) => {
        return (
            <View style={styles.main}>

                <View style={styles.withinMain}>
                    <View style={styles.imageBoundary}>
                        <Image style={{
                            flex: 1,
                            resizeMode: 'contain',
                            height: 90,
                            width: 80,
                        }}
                            imageStyle={{
                                height: 90,
                                width: 80,
                            }}
                            source={list.img} />
                    </View>
                    <View style={styles.textBoundary}>
                        <Text style={styles.topText}>{list.topText}</Text>
                        <Text style={styles.midText}>{list.midText1} {'\n'}{list.midText2}</Text>
                        <Text style={styles.downText}>{list.price}</Text>
                    </View>
                    <View style={{ top: 35 }}><Text>  <Icons.PlusCircleIcon style={{ color: COLORS.green }} /></Text></View>
                </View>


            </View>
        )
    }

    return (
        <View >
            <FlatList data={list}
                renderItem={({ item }) => <Menu list={item} />}
            />
        </View >
    )
}

const styles = StyleSheet.create({
    main: {
        backgroundColor: COLORS.white,
        width: '100%',
        borderRadius: 5,
        height: 110,
        marginBottom: 12
    },
    withinMain: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        margin: 10
    },
    imageBoundary: {
        height: 90,
        width: 80,
        borderRadius: 10
    },
    textBoundary: {
        paddingRight: 30,
        justifyContent: 'space-between',
        height: 90
    },
    topText: {
        fontWeight: 'bold',
        fontSize: 18
    },
    midText: {
        color: 'grey',
        fontSize: 13
    },
    downText: {
        color: COLORS.green,
        fontWeight: 'bold',
        fontSize: 18
    }

})
