import React, { Component } from 'react'
import { View, Text, Image, Dimensions, TouchableOpacity } from 'react-native'
import AppIntroSlider from 'react-native-app-intro-slider'
import { RenderItem } from 'react-native'
import BoldText, { RegularText } from '../components/Shared/StyledText'
import Item from '../components/menu/Item'
import COLORS from '../assets/colors/colors'

const slider = [
    {
        id: 1,
        title: 'Delicious Food',
        text: 'Order meals and enjoy food from BAsement Plus',
        img: require('../assets/bg1.jpg'),


    },
    {
        id: 2,
        title: 'Fast Delivery',
        text: 'Order meals and enjoy food from BAsement Plus',
        img: require('../assets/bg1.jpg'),


    },
    {
        id: 3,
        title: 'Easy payment',
        text: 'Order meals and enjoy food from BAsement Plus',
        img: require('../assets/cart.jpeg'),
        login: 'Sign-up / Login'
    }


]


export default class Slider extends Component {


    renderItem = ({ item }) => {
        return (
            <View style={{ flex: 1, marginBottom: 20 }}>
                <Image
                    source={item.img}
                    style={{
                        resizeMode: 'contain',
                        width: Dimensions.get('window').width,
                        height: Dimensions.get('window').height - 100

                    }}
                />
                <View>
                    <BoldText
                        style={{
                            alignSelf: 'center',
                            fontSize: 40,
                            color: COLORS.main
                        }}>{item.title}</BoldText>
                    <RegularText>{item.text}</RegularText>
                </View>

            </View>
        )
    }


    render() {
        return (
            <View
                style={{
                    height: '95%'
                }}>
                <AppIntroSlider
                    renderItem={this.renderItem}
                    data={slider}

                />
                <View>
                    <TouchableOpacity style={{
                        backgroundColor: 'black',
                        height: 20
                    }}
                        onPress={() => this.props.navigation.navigate('Home')}>
                        <Text>Sign Up / Login</Text>
                    </TouchableOpacity>
                </View>
            </View>
        )
    }

}
