import React, { useContext } from 'react'
import { View, Text, TouchableOpacity, StyleSheet, Platform, StatusBar, SafeAreaView, FlatList, TextInput } from 'react-native'
import { CartContext } from '../../context/CartContext'
import OrdersTabs from './OrdersTab';
import { HeaderButton } from './OrdersTab';
import BoldText, { ExtraBoldText, RegularText } from '../../components/Shared/StyledText';
import { Entypo } from '@expo/vector-icons';
import Ionicons from 'react-native-vector-icons/Ionicons'

import COLORS from '../../assets/colors/colors';
import Input from '../../components/Shared/Input';

export default function ConfirmScreen() {

    const [cart, setCart] = useContext(CartContext);
    const totalPrice = cart.reduce((acc, curr) => acc + curr.price * curr.count, 0)
    return (
        <View>
            <OrdersTabs />
            <View
                style={{
                    marginHorizontal: 10,
                }}>
                <ExtraBoldText
                    style={{
                        fontSize: 15,
                        marginBottom: 10,
                    }}>Delivery</ExtraBoldText>
                <View
                    style={styles.display}>
                    <Entypo name="location-pin" size={25} color="grey"
                        style={{

                            alignSelf: 'center',
                            marginLeft: 10
                        }} />



                </View>

                <View
                    style={styles.display}>
                    <Ionicons name="phone-portrait-outline" size={25} color="grey"
                        style={{
                            // width: '20%',
                            alignSelf: 'center',
                            marginLeft: 10
                        }} />
                    <TextInput
                        style={{
                            height: '100%',
                            width: '90%',
                            outline: 'none'
                        }}
                        keyboardType={'number-pad'}
                        maxLength={14} />
                </View>

                <FlatList
                    showsVerticalScrollIndicator={false}
                    data={cart}
                    renderItem={({ item }) =>
                        <View>
                            <RegularText >{item.name}</RegularText>
                        </View>
                    }
                />
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    display: {
        height: 40,
        width: '100%',
        alignSelf: 'center',
        backgroundColor: 'white',
        borderTopWidth: 3,
        borderTopColor: '#f5f5f5',
        flexDirection: 'row',
        // justifyContent: 'space-between',

        marginBottom: 15,
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.30,
        shadowRadius: 4.65,

        elevation: 8,

    }
})
