import React from 'react'
import { View, Text, Image, ScrollView } from 'react-native';

const items = [
    {
        image: require("../assets/deals.png"),
        text: "Pick-up",
    },
    {
        image: require("../assets/soft-drink.png"),
        text: "Soft Drinks",
    }, {
        image: require("../assets/bread.png"),
        text: "Bakery Items",
    },
    {
        image: require("../assets/fast-food.png"),
        text: "Fast Foods",
    },
    {
        image: require("../assets/deals.png"),
        text: "Deals",
    },
    {
        image: require("../assets/coffee.png"),
        text: "Coffee & Tea",
    },
    {
        image: require("../assets/desserts.png"),
        text: "Desserts",
    },
]

export default function Categories() {
    return (
        <View
            style={{
                marginBottom: 20
            }}>
            <ScrollView horizontal showsHorizontalScrollIndicator={true}>
                {items.map((item, index) => (
                    <View key={index} style={{ alignItems: 'center', marginRight: 7, height: 20, width: 20, backgroundColor: 'white' }}>
                        <Image source={item.image}
                            style={{
                                width: 20,
                                height: 20,
                                resizeMode: 'contain'
                            }} />
                        <Text style={{
                            fontSize: 8,
                            fontWeight: '900'
                        }}
                        >{item.text}
                        </Text>
                    </View>
                ))}

            </ScrollView>
        </View>
    )
}
