import React, { useContext } from 'react'
import { View, Text, TouchableOpacity, StyleSheet, Platform, StatusBar, SafeAreaView, FlatList } from 'react-native'
import HeaderAnnex from '../components/Shared/HeaderAnnex'
import { CartContext } from '../context/CartContext';

export default function OrderScreen({ navigation }) {

    const [cart, setCart] = useContext(CartContext);
    const totalPrice = cart.reduce((acc, curr) => acc + curr.price * curr.count, 0)

    return (
        <SafeAreaView style={styles.androidSafeArea}>
            <HeaderAnnex titleAlign="center" title="Complaints and Enquiries" />
            <FlatList
                showsVerticalScrollIndicator={false}
                data={cart}
                renderItem={({ item }) =>
                    <View >
                        <Text >{item.name}</Text>
                        <View>
                            <Text>{totalPrice}</Text>

                        </View>
                    </View>


                }
            />
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    androidSafeArea: {
        flex: 1,
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
    },

})
