import React, { useContext } from 'react'
import { View, Text, TouchableOpacity, StyleSheet, Platform, StatusBar, SafeAreaView, FlatList } from 'react-native'
import { CartContext } from '../../context/CartContext'
import OrdersTabs from './OrdersTab';
import activeTab from './OrdersTab'
import BoldText, { ExtraBoldText, RegularText } from '../../components/Shared/StyledText';

export default function ConfirmScreen() {

    const [cart, setCart] = useContext(CartContext);
    const totalPrice = cart.reduce((acc, curr) => acc + curr.price * curr.count, 0)
    return (
        <View>
            <OrdersTabs />
            <View
                style={{
                    marginHorizontal: 10,
                }}>
                <ExtraBoldText
                    style={{
                        fontSize: 15,
                        marginBottom: 10,

                    }}>Delivery</ExtraBoldText>
                <View
                    style={styles.display}>
                </View>
                <FlatList
                    showsVerticalScrollIndicator={false}
                    data={cart}
                    renderItem={({ item }) =>
                        <View>
                            <RegularText >{item.name}</RegularText>
                        </View>
                    }
                />
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    display: {
        height: activeTab == "DELIVERY" ? 40 : 0,
        width: '100%',
        alignSelf: 'center',
        backgroundColor: 'white',
        borderTopWidth: 3,
        borderTopColor: '#f5f5f5',
        marginBottom: 12,
        shadowOffset: {
            width: 0,
            height: 4,
        },
        shadowOpacity: 0.30,
        shadowRadius: 4.65,

        elevation: 8,

    }
})
